ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
	statusCode: p('http.get.status.ok'),
	message: p('http.get.message.ok'),
	searchCriteria: vars.songTitle,
	songs: payload map ( songs ) -> {
		songId: songs.SONG_ID,
		songTitle: songs.SONG_TITLE,
		genre: songs.GENRE default "",
		publicationDate: songs.PUBLICATION_DT as String{format: "yyyy-MM-dd"} default "",
		duration: songs.DURATION default "",
		albumId: (songs.ALBUM_ID default 0),
		albumName: songs.ALBUM_NM default "",
		songWriters: [{
			writer: songs.GROUP_NM default ""
		}]
	}
}
